document.getElementById("specific sentence based on randomnumbertree").addEventListener("click", randomSentenceBasedOn)

function randomSentenceBasedOn() {
    function randomSentence() {
        let wordsSplit = lorem.split("").join("")
        let wordsSlice = wordsSplit.slice(0, getTextInit())
        return wordsSlice
    }

    document.getElementById("random sentence printer").innerText = randomSentence()
}






//comparing two words weather they are of same length or not\

document.getElementById("compare two strings").addEventListener("click", compare)
function compare() {
    let string1 = document.getElementById("firstword").value
    let string2 = document.getElementById("secondword").value
    document.getElementById("result text field").innerHTML = validatestrings(string1, string2)
}

function validatestrings(word1, word2) {


    if (word1.length === word2.length) {
        document.getElementById("result text field").style.backgroundColor = "green"
        return ("both lengths are same")

    }
    else {
        document.getElementById("result text field").style.backgroundColor = "red"
        return ("both lengths are not same")

    }
}















